QueryBlock.Query:
INSERT INTO {РегНакопичення_Підсумки_TablePart.TABLE} ( uid, {РегНакопичення_Підсумки_TablePart.Користувач}, {РегНакопичення_Підсумки_TablePart.Сума} ) SELECT uuid_generate_v4(), РегНакопичення.{РегНакопичення_Залишки_TablePart.Користувач} AS Користувач,
/* Сума */ SUM(РегНакопичення.{РегНакопичення_Залишки_TablePart.Сума}) AS Сума
FROM {РегНакопичення_Залишки_TablePart.TABLE} AS РегНакопичення
GROUP BY Користувач
HAVING
/* Сума */ SUM(РегНакопичення.{РегНакопичення_Залишки_TablePart.Сума}) != 0
